import java.util.Scanner;
public class JPD302_2 {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int i = 1, j = 1, count = 0;
        for (i = 1; i <= 3; i++) {        //第一個迴圈，i從1到3
            for (j = 1; j <= 9; j++) {        //第二個迴圈，j從1到9
                count++;
                }
            }
        System.out.printf("count = %d\n",count);
        }
    }